mapper.select:
SELECT d."id" AS "id", d."name" AS "name", d."sex" AS "sex", d."tel" AS "tel", d."school" AS "school", d."degree" AS "degree", d."job" AS "job", md."name" AS "deptName", ds."name" AS "subName", d."recommended" AS "recommended", d."status" AS "status" FROM HOSPITAL.DOCTOR d JOIN HOSPITAL.MEDICAL_DEPT_SUB_AND_DOCTOR sd ON sd."doctor_id" = d."id" JOIN HOSPITAL.MEDICAL_DEPT_SUB ds ON sd."dept_sub_id" = ds."id" JOIN HOSPITAL.MEDICAL_DEPT md ON ds."dept_id" = md."id" WHERE 1=1
AND d."name" LIKE '%${name}%'
AND md."id" = ${deptId}
AND d."degree" = #{degree}
AND d."job" = #{job}
AND d."recommended" = ${recommended}
AND d."status" = ${status}
ORDER BY md."id" ${order}
LIMIT ${length} OFFSET #{start}

SELECT COUNT(*)
FROM HOSPITAL.DOCTOR d
JOIN HOSPITAL.MEDICAL_DEPT_SUB_AND_DOCTOR sd ON sd."doctor_id" = d."id"
JOIN HOSPITAL.MEDICAL_DEPT_SUB ds ON sd."dept_sub_id" = ds."id"
JOIN HOSPITAL.MEDICAL_DEPT md ON ds."dept_id" = md."id"
WHERE 1=1

    AND d."name" LIKE '%${name}%'


    AND md."id" = ${deptId}


    AND d."degree" = #{degree}


    AND d."job" = #{job}


    AND d."recommended" = ${recommended}

AND d."status" = ${status}
