mapper.insert:
    UPSERT INTO HOSPITAL.DOCTOR_PRESCRIPTION(
    "id", "uuid", "patient_card_id", "diagnosis", "sub_dept_id", "doctor_id", "registration_id", "rp"
)
VALUES (
    NEXT VALUE FOR HOSPITAL.DOCTOR_PRESCRIPTION_SEQUENCE, #{uuid}, ${patientCardId}, #{diagnosis},
    ${subDeptId}, ${doctorId}, ${registrationId}, #{rp}
    )
