mapper.select:
SELECT ds."id" AS "id", ds."name" AS "subName", d."name" AS "deptName", ds."location" AS "location", t1."count" AS "doctors", t2."count" AS "masterDoctors", t3."count" AS "generalDoctors" FROM HOSPITAL.MEDICAL_DEPT_SUB ds JOIN HOSPITAL.MEDICAL_DEPT d ON ds."dept_id" = d."id" JOIN ( SELECT ds."id", COUNT(sd."id") AS "count" FROM HOSPITAL.MEDICAL_DEPT_SUB ds LEFT JOIN HOSPITAL.MEDICAL_DEPT_SUB_AND_DOCTOR sd ON sd."dept_sub_id" = ds."id" LEFT JOIN HOSPITAL.DOCTOR d ON sd."doctor_id" = d."id" AND d."status" = 1 GROUP BY ds."id" ) t1 ON t1."id" = ds."id" LEFT JOIN ( SELECT ds."id", COUNT(d."id") AS "count" FROM HOSPITAL.MEDICAL_DEPT_SUB ds LEFT JOIN HOSPITAL.MEDICAL_DEPT_SUB_AND_DOCTOR sd ON sd."dept_sub_id" = ds."id" LEFT JOIN HOSPITAL.DOCTOR d ON d."id" = sd."doctor_id" AND d."job" IN ('主任医师','副主任医师') AND d."status" = 1 GROUP BY ds."id" ) t2 ON t2."id" = ds."id" LEFT JOIN ( SELECT ds."id", COUNT(d."id") AS "count" FROM HOSPITAL.MEDICAL_DEPT_SUB ds LEFT JOIN HOSPITAL.MEDICAL_DEPT_SUB_AND_DOCTOR sd ON sd."dept_sub_id" = ds."id" LEFT JOIN HOSPITAL.DOCTOR d ON d."id" = sd."doctor_id" AND d."job" IN ('主治医师','副主治医师') AND d."status" = 1 GROUP BY ds."id" ) t3 ON t3."id" = ds."id" WHERE 1 = 1
AND ds."name" LIKE '%${name}%'
AND d."id" = ${deptId}
ORDER BY d."id" ${order}
LIMIT ${length} OFFSET ${start}

SELECT COUNT(*) FROM HOSPITAL.MEDICAL_DEPT_SUB ds JOIN HOSPITAL.MEDICAL_DEPT d ON ds."dept_id" = d."id" WHERE 1 = 1
AND ds."name" LIKE '%${name}%'
AND d."id" = ${deptId}
